import java.awt.*;public class RecursionExercise {    public static void main (String[]args){        int day = 10;        Peach peach = new Peach();        peach.peach(10);        System.out.println(peach.peach(1));    }}class Peach{    public int peach(int day) {        if(day == 10) {            return 1;        } else if ( day >= 1 && day <=9 ) {            return (peach(day + 1) + 1) * 2;        } else {            System.out.println("day åœ¨1-10");            return -1;        }    }}